import React, { useState, useEffect } from 'react';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import { LayoutDashboard, MessageSquare, PlusCircle, LogOut, User, Menu, X, ShoppingBag, Sun, Moon } from 'lucide-react';
import { db } from '../firebase';
import { collection, query, where, onSnapshot } from 'firebase/firestore';

const SidebarContent = ({ navItems, location, setIsMobileMenuOpen, unreadCount, toggleTheme, theme, currentUser, handleLogout }) => (
    <div className="flex flex-col h-full bg-white dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 transition-colors duration-300">
        <div className="p-6 flex items-center justify-between">
            <div className="flex items-center gap-3">
                <div className="w-8 h-8 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-lg flex items-center justify-center text-white font-bold text-lg shadow-lg shadow-blue-500/30">
                    M
                </div>
                <span className="text-xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-gray-900 to-gray-600 dark:from-white dark:to-gray-400">
                    Mini Mart
                </span>
            </div>
            <button
                onClick={toggleTheme}
                className="p-2 rounded-lg bg-gray-100 dark:bg-gray-800 text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors"
            >
                {theme === 'light' ? <Moon size={18} /> : <Sun size={18} />}
            </button>
        </div>

        <nav className="flex-1 px-4 space-y-2 py-4">
            {navItems.map((item) => {
                const isActive = location.pathname === item.path;
                return (
                    <NavLink
                        key={item.path}
                        to={item.path}
                        onClick={() => setIsMobileMenuOpen(false)}
                        className={`flex items-center gap-3 px-4 py-3 rounded-xl transition-all duration-300 relative overflow-hidden group ${isActive
                            ? 'text-blue-600 bg-blue-50 dark:bg-blue-900/20 shadow-sm'
                            : 'text-gray-600 dark:text-gray-400 hover:bg-gray-50 dark:hover:bg-gray-800 hover:text-gray-900 dark:hover:text-white'
                            }`}
                    >
                        {isActive && (
                            <motion.div
                                layoutId="activeTab"
                                className="absolute left-0 top-0 bottom-0 w-1 bg-blue-600 rounded-r-full"
                            />
                        )}

                        <div className="relative">
                            <item.icon size={20} className={isActive ? 'stroke-[2.5px]' : 'stroke-2'} />
                            {item.name === 'Messages' && unreadCount > 0 && (
                                <span className=\"absolute -top-1 -right-1 flex h-2.5 w-2.5\">
                                    <span className=\"animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75\"></span>
                                    <span className=\"relative inline-flex rounded-full h-2.5 w-2.5 bg-red-500\"></span>
                                </span>
                            )}
                        </div>
                        <span className="font-medium">{item.name}</span>
                    </NavLink>
                );
            })}
        </nav>

        <div className="p-4 border-t border-gray-100 dark:border-gray-800">
            <div className="flex items-center gap-3 px-4 py-3 mb-2">
                <div className="w-8 h-8 rounded-full bg-gradient-to-r from-purple-500 to-pink-500 flex items-center justify-center text-white text-xs font-bold ring-2 ring-white dark:ring-gray-800 shadow-md overflow-hidden">
                    {currentUser?.photoURL ? (
                        <img src={currentUser.photoURL} alt="Profile" className="w-full h-full object-cover" />
                    ) : (
                        currentUser?.email?.[0].toUpperCase()
                    )}
                </div>
                <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-gray-900 dark:text-white truncate">
                        {currentUser?.email?.split('@')[0]}
                    </p>
                    <p className="text-xs text-gray-500 dark:text-gray-400 truncate">
                        Online
                    </p>
                </div>
            </div>
            <button
                onClick={handleLogout}
                className="flex items-center gap-3 px-4 py-2 w-full text-red-600 hover:bg-red-50 dark:hover:bg-red-900/20 rounded-lg transition-colors text-sm font-medium"
            >
                <LogOut size={18} />
                Sign Out
            </button>
        </div>
    </div>
);

export default function Layout({ children }) {
    const { currentUser, logout } = useAuth();
    const { theme, toggleTheme } = useTheme();
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const location = useLocation();
    const navigate = useNavigate();

    const handleLogout = async () => {
        try {
            await logout();
            navigate('/login');
        } catch {
            console.error("Failed to log out");
        }
    };

    // Calculate unread messages
    const [unreadCount, setUnreadCount] = useState(0);

    useEffect(() => {
        if (!currentUser) return;

        const q = query(
            collection(db, "chats"),
            where("participants", "array-contains", currentUser.uid)
        );

        const unsubscribe = onSnapshot(q, (snapshot) => {
            let totalUnread = 0;
            snapshot.docs.forEach(doc => {
                const data = doc.data();
                if (data.unreadCounts && data.unreadCounts[currentUser.uid]) {
                    totalUnread += data.unreadCounts[currentUser.uid];
                }
            });
            setUnreadCount(totalUnread);
        });

        return unsubscribe;
    }, [currentUser]);

    const navItems = [
        { name: 'Browse', path: '/', icon: ShoppingBag },
        { name: 'Sell Item', path: '/create-listing', icon: PlusCircle },
        { name: 'Messages', path: '/chat', icon: MessageSquare },
        { name: 'Profile', path: '/profile', icon: User },
    ];

    return (
        <div className="min-h-screen bg-gray-50/50 dark:bg-gray-950 flex transition-colors duration-300">
            {/* Desktop Sidebar */}
            <div className="hidden md:block w-64 h-screen sticky top-0 z-50">
                <SidebarContent
                    navItems={navItems}
                    location={location}
                    setIsMobileMenuOpen={setIsMobileMenuOpen}
                    unreadCount={unreadCount}
                    toggleTheme={toggleTheme}
                    theme={theme}
                    currentUser={currentUser}
                    handleLogout={handleLogout}
                />
            </div>

            {/* Mobile Header */}
            <div className="md:hidden fixed top-0 left-0 right-0 z-50 bg-white dark:bg-gray-900 border-b border-gray-200 dark:border-gray-800 px-4 py-3 flex items-center justify-between transition-colors">
                <div className="flex items-center gap-2">
                    <div className="w-8 h-8 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-lg flex items-center justify-center text-white font-bold text-lg">
                        M
                    </div>
                    <span className="font-bold text-gray-900 dark:text-white">Mini Mart</span>
                </div>
                <div className="flex items-center gap-2">
                    <button
                        onClick={toggleTheme}
                        className="p-2 text-gray-600 dark:text-gray-300 bg-gray-100 dark:bg-gray-800 rounded-lg"
                    >
                        {theme === 'light' ? <Moon size={18} /> : <Sun size={18} />}
                    </button>
                    <button
                        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                        className="p-2 text-gray-600 dark:text-gray-300 bg-gray-100 dark:bg-gray-800 rounded-lg relative"
                    >
                        {isMobileMenuOpen ? <X size={20} /> : <Menu size={20} />}
                        {!isMobileMenuOpen && unreadCount > 0 && (
                            <span className="absolute -top-1 -right-1 flex h-3 w-3">
                                <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75"></span>
                                <span className="relative inline-flex rounded-full h-3 w-3 bg-red-500 border-2 border-white"></span>
                            </span>
                        )}
                    </button>
                </div>
            </div>

            {/* Mobile Drawer */}
            <AnimatePresence>
                {isMobileMenuOpen && (
                    <>
                        <motion.div
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            exit={{ opacity: 0 }}
                            onClick={() => setIsMobileMenuOpen(false)}
                            className="md:hidden fixed inset-0 bg-black/50 z-40 backdrop-blur-sm"
                        />
                        <motion.div
                            initial={{ x: '-100%' }}
                            animate={{ x: 0 }}
                            exit={{ x: '-100%' }}
                            transition={{ type: "spring", damping: 25, stiffness: 200 }}
                            className="md:hidden fixed inset-y-0 left-0 w-[280px] bg-white dark:bg-gray-900 z-50 shadow-2xl"
                        >
                            <SidebarContent
                                navItems={navItems}
                                location={location}
                                setIsMobileMenuOpen={setIsMobileMenuOpen}
                                unreadCount={unreadCount}
                                toggleTheme={toggleTheme}
                                theme={theme}
                                currentUser={currentUser}
                                handleLogout={handleLogout}
                            />
                        </motion.div>
                    </>
                )}
            </AnimatePresence>

            {/* Main Content */}
            <main className="flex-1 min-w-0 md:p-8 p-4 pt-20 md:pt-8">
                <motion.div
                    key={location.pathname}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3 }}
                    className="max-w-6xl mx-auto"
                >
                    {children}
                </motion.div>
            </main>
        </div>
    );
}

import React, { useState, useEffect, useRef } from 'react';
import { db } from '../firebase';
import { doc, onSnapshot, setDoc, updateDoc, deleteDoc, addDoc, collection, getDoc, serverTimestamp, increment } from 'firebase/firestore';
import { Video, Mic, MicOff, VideoOff, PhoneOff } from 'lucide-react';

// WebRTC Configuration
const servers = {
    iceServers: [
        {
            urls: [
                'stun:stun1.l.google.com:19302',
                'stun:stun2.l.google.com:19302',
                'stun:stun.l.google.com:19302',
                'stun:stun3.l.google.com:19302',
                'stun:stun4.l.google.com:19302',
            ],
        },
    ],
    iceCandidatePoolSize: 10,
};

export default function VideoCall({ chatId, currentUser, targetUser, callType = 'video', onClose }) {
    const [, setPc] = useState(null);
    const [, setLocalStream] = useState(null); // Keep state but unused in render for now
    const [, setRemoteStream] = useState(null); // Keep state for consistency
    const [status, setStatus] = useState("initializing"); // initializing, calling, connected, incoming, offering, answered
    const [isMuted, setIsMuted] = useState(false);
    const [isVideoOff, setIsVideoOff] = useState(callType === 'audio');
    const [isCaller, setIsCaller] = useState(false);

    const localVideoRef = useRef();
    const remoteVideoRef = useRef();

    // Refs for state that needs to be accessed in async callbacks/cleanup without stale closures
    const pcRef = useRef(null);
    const localStreamRef = useRef(null);
    const candidateQueue = useRef([]); // Queue for ICE candidates

    useEffect(() => {
        let unsubscribeDoc = null;
        let unsubscribeCandidates = null;
        let canceled = false;

        const startCall = async () => {
            const pc = new RTCPeerConnection(servers);
            pcRef.current = pc;
            setPc(pc);

            // Get Local Stream
            const constraints = {
                audio: true,
                video: callType === 'video'
            };

            try {
                const stream = await navigator.mediaDevices.getUserMedia(constraints);
                if (canceled) {
                    stream.getTracks().forEach(track => track.stop());
                    return;
                }

                localStreamRef.current = stream;
                setLocalStream(stream);

                if (localVideoRef.current && callType === 'video') {
                    localVideoRef.current.srcObject = stream;
                }

                stream.getTracks().forEach(track => pc.addTrack(track, stream));
            } catch (err) {
                console.error("Error accessing media devices:", err);
                if (!canceled) {
                    alert("Could not access camera/microphone. Please check permissions.");
                    onClose();
                }
                return;
            }

            // Handle Remote Stream
            pc.ontrack = (event) => {
                const stream = event.streams[0] || new MediaStream();
                if (!event.streams[0]) stream.addTrack(event.track);

                setRemoteStream(stream);
                if (remoteVideoRef.current) {
                    remoteVideoRef.current.srcObject = stream;
                }
            };

            const callDocRef = doc(db, "chats", chatId, "calls", "active_call");
            const offerCandidates = collection(callDocRef, 'offerCandidates');
            const answerCandidates = collection(callDocRef, 'answerCandidates');

            const callDocSnapshot = await getDoc(callDocRef);
            if (canceled) return;

            if (!callDocSnapshot.exists()) {
                // --- CALLER LOGIC ---
                setIsCaller(true);
                setStatus("calling");

                pc.onicecandidate = (event) => {
                    if (event.candidate) {
                        addDoc(offerCandidates, event.candidate.toJSON());
                    }
                };

                const offerDescription = await pc.createOffer();
                await pc.setLocalDescription(offerDescription);

                await setDoc(callDocRef, {
                    offer: { type: offerDescription.type, sdp: offerDescription.sdp },
                    callerId: currentUser.uid,
                    callerName: currentUser.displayName || 'Seller',
                    calleeId: targetUser.uid,
                    calleeName: targetUser.name,
                    callType: callType,
                    status: "offering",
                    createdAt: serverTimestamp()
                });

                // Listen for Answer
                unsubscribeDoc = onSnapshot(callDocRef, async (snapshot) => {
                    if (canceled) return;
                    if (!snapshot.exists()) {
                        onClose();
                        return;
                    }

                    const data = snapshot.data();
                    if (!pc.currentRemoteDescription && data?.answer) {
                        const answerDescription = new RTCSessionDescription(data.answer);
                        await pc.setRemoteDescription(answerDescription);
                        setStatus("connected");

                        // Flush ICE candidates
                        while (candidateQueue.current.length > 0) {
                            const candidate = candidateQueue.current.shift();
                            pc.addIceCandidate(candidate).catch(e => console.error("Error flushing candidate", e));
                        }
                    }
                });

                // Listen for Answer Candidates
                unsubscribeCandidates = onSnapshot(answerCandidates, (snapshot) => {
                    snapshot.docChanges().forEach((change) => {
                        if (change.type === 'added') {
                            const candidate = new RTCIceCandidate(change.doc.data());
                            if (pc.remoteDescription) {
                                pc.addIceCandidate(candidate).catch(e => console.error("Error adding candidate", e));
                            } else {
                                candidateQueue.current.push(candidate);
                            }
                        }
                    });
                });

            } else {
                // --- CALLEE LOGIC ---
                setIsCaller(false);
                setStatus("connecting");
                const data = callDocSnapshot.data();

                pc.onicecandidate = (event) => {
                    if (event.candidate) {
                        addDoc(answerCandidates, event.candidate.toJSON());
                    }
                };

                // Listen for Hangup
                unsubscribeDoc = onSnapshot(callDocRef, (snapshot) => {
                    if (canceled) return;
                    if (!snapshot.exists()) onClose();
                });

                await pc.setRemoteDescription(new RTCSessionDescription(data.offer));

                // Flush candidates
                while (candidateQueue.current.length > 0) {
                    const candidate = candidateQueue.current.shift();
                    pc.addIceCandidate(candidate).catch(e => console.error("Error flushing candidate", e));
                }

                const answerDescription = await pc.createAnswer();
                await pc.setLocalDescription(answerDescription);

                await updateDoc(callDocRef, {
                    answer: { type: answerDescription.type, sdp: answerDescription.sdp },
                    status: "answered"
                });

                setStatus("connected");

                // Listen for Offer Candidates
                unsubscribeCandidates = onSnapshot(offerCandidates, (snapshot) => {
                    snapshot.docChanges().forEach((change) => {
                        if (change.type === 'added') {
                            const candidate = new RTCIceCandidate(change.doc.data());
                            if (pc.remoteDescription) {
                                pc.addIceCandidate(candidate).catch(e => console.error("Error adding candidate", e));
                            } else {
                                candidateQueue.current.push(candidate);
                            }
                        }
                    });
                });
            }
        };

        startCall();

        return () => {
            canceled = true;
            if (localStreamRef.current) localStreamRef.current.getTracks().forEach(track => track.stop());
            if (pcRef.current) pcRef.current.close();
            if (unsubscribeDoc) unsubscribeDoc();
            if (unsubscribeCandidates) unsubscribeCandidates();
        }
    }, [chatId, callType, currentUser, targetUser, onClose]);

    const toggleMute = () => {
        if (localStreamRef.current) {
            const enabled = !isMuted;
            setIsMuted(enabled);
            localStreamRef.current.getAudioTracks().forEach(track => track.enabled = !enabled);
        }
    };

    const toggleVideo = () => {
        if (localStreamRef.current) {
            const enabled = !isVideoOff;
            setIsVideoOff(enabled);
            localStreamRef.current.getVideoTracks().forEach(track => track.enabled = !enabled);
        }
    };

    const handleEndCall = async () => {
        if (isCaller && status === 'calling') {
            try {
                // Log missed call
                await addDoc(collection(db, "chats", chatId, "messages"), {
                    text: `Missed ${callType} call`,
                    senderId: currentUser.uid,
                    isSystemMessage: true,
                    timestamp: serverTimestamp()
                });

                await updateDoc(doc(db, "chats", chatId), {
                    lastMessage: `Missed ${callType} call`,
                    lastMessageSenderId: currentUser.uid,
                    lastMessageTimestamp: serverTimestamp(),
                    [`unreadCounts.${targetUser.uid}`]: increment(1)
                });
            } catch (error) {
                console.error("Error logging missed call:", error);
            }
        }

        try {
            await deleteDoc(doc(db, "chats", chatId, "calls", "active_call"));
        } catch (e) { /* empty catch */ }

        onClose();
    };

    return (
        <div className="fixed inset-0 z-[100] bg-gray-900 text-white overflow-hidden">
            <div className="absolute inset-0 w-full h-full">
                <video
                    ref={remoteVideoRef}
                    autoPlay
                    playsInline
                    className={`w-full h-full object-cover transition-opacity duration-500 ${(status === 'connected' || status === 'connecting') ? 'opacity-100' : 'opacity-0'}`}
                />

                {status !== 'connected' && (
                    <div className="absolute inset-0 bg-gray-900 flex flex-col items-center justify-center">
                        <div className="absolute inset-0 bg-gradient-to-br from-blue-900/40 to-purple-900/40 animate-pulse" />
                        <div className="relative z-10 mb-8">
                            <div className="w-32 h-32 rounded-full overflow-hidden border-4 border-white/10 shadow-2xl bg-gray-800 flex items-center justify-center text-4xl font-bold">
                                {targetUser.name?.[0]?.toUpperCase()}
                            </div>
                            <div className="absolute inset-0 -z-10 rounded-full border border-white/20 scale-125 animate-[ping_2s_ease-out_infinite]" />
                        </div>
                        <div className="relative z-10 text-center space-y-2">
                            <h2 className="text-3xl font-bold">{targetUser.name}</h2>
                            <p className="text-lg text-blue-200 font-medium animate-pulse">
                                {status === 'calling' ? 'Calling...' : status === 'initializing' ? 'Initializing...' : 'Connecting...'}
                            </p>
                        </div>
                    </div>
                )}
            </div>

            {status === 'connected' && (
                <div className="absolute top-0 left-0 right-0 p-6 pt-12 bg-gradient-to-b from-black/70 to-transparent z-20 flex justify-between items-start pointer-events-none">
                    <div className="flex items-center gap-3">
                        <div className="w-10 h-10 rounded-full bg-gray-700/50 backdrop-blur border border-white/20 flex items-center justify-center font-bold">
                            {targetUser.name?.[0]}
                        </div>
                        <div>
                            <h3 className="font-bold text-lg leading-tight">{targetUser.name}</h3>
                            <p className="text-xs text-white/70">Connected</p>
                        </div>
                    </div>
                </div>
            )}

            {callType === 'video' && (
                <div className="absolute top-4 right-4 z-30 w-32 md:w-48 aspect-[3/4] md:aspect-video rounded-2xl overflow-hidden shadow-2xl border border-white/20 bg-gray-800">
                    <video
                        ref={localVideoRef}
                        autoPlay
                        playsInline
                        muted
                        className={`w-full h-full object-cover ${isVideoOff ? 'hidden' : ''}`}
                    />
                    {isVideoOff && (
                        <div className="w-full h-full flex flex-col items-center justify-center bg-gray-900 text-gray-500 gap-2">
                            <VideoOff size={24} />
                            <span className="text-[10px] uppercase font-bold">Camera Off</span>
                        </div>
                    )}
                </div>
            )}

            <div className="absolute bottom-10 left-0 right-0 z-30 flex justify-center items-end pb-8 px-4">
                <div className="flex items-center gap-4 bg-black/40 backdrop-blur-xl border border-white/10 p-3 rounded-3xl shadow-2xl pointer-events-auto">
                    <button
                        onClick={toggleMute}
                        className={`p-4 rounded-full transition-all ${isMuted ? 'bg-white text-gray-900' : 'bg-white/10 text-white hover:bg-white/20'}`}
                    >
                        {isMuted ? <MicOff size={24} /> : <Mic size={24} />}
                    </button>

                    {callType === 'video' && (
                        <button
                            onClick={toggleVideo}
                            className={`p-4 rounded-full transition-all ${isVideoOff ? 'bg-white text-gray-900' : 'bg-white/10 text-white hover:bg-white/20'}`}
                        >
                            {isVideoOff ? <VideoOff size={24} /> : <Video size={24} />}
                        </button>
                    )}

                    <button
                        onClick={handleEndCall}
                        className="p-4 rounded-full bg-red-500 text-white shadow-lg hover:bg-red-600 active:scale-95 transition-all mx-2"
                    >
                        <PhoneOff size={28} />
                    </button>
                </div>
            </div>
        </div>
    );
}

import { createContext, useContext, useEffect, useState } from "react";
import {
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword,
    signOut,
    onAuthStateChanged,
    updateProfile,
    sendPasswordResetEmail
} from "firebase/auth";
import { auth, db } from "../firebase";
import { doc, setDoc, serverTimestamp } from "firebase/firestore";

const AuthContext = createContext();

export function AuthProvider({ children }) {
    const [currentUser, setCurrentUser] = useState(null);
    const [loading, setLoading] = useState(true);

    function signup(email, password) {
        return createUserWithEmailAndPassword(auth, email, password);
    }

    function login(email, password) {
        return signInWithEmailAndPassword(auth, email, password);
    }

    function logout() {
        return signOut(auth);
    }

    function updateUserProfile(user, data) {
        return updateProfile(user, data).then(() => {
            setCurrentUser({ ...user }); // Force state update
        });
    }

    function resetPassword(email) {
        return sendPasswordResetEmail(auth, email);
    }

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async (user) => {
            setCurrentUser(user);
            setLoading(false);

            if (user) {
                // Presence Heartbeat
                const userRef = doc(db, "users", user.uid);
                await setDoc(userRef, {
                    email: user.email,
                    displayName: user.displayName || user.email.split('@')[0],
                    photoURL: user.photoURL,
                    lastSeen: serverTimestamp(),
                    isOnline: true
                }, { merge: true });
            }
        });

        return unsubscribe;
    }, []);

    // Periodic Heartbeat
    useEffect(() => {
        if (!currentUser) return;

        const interval = setInterval(async () => {
            try {
                await setDoc(doc(db, "users", currentUser.uid), {
                    lastSeen: serverTimestamp(),
                    isOnline: true
                }, { merge: true });
            } catch (err) {
                console.error("Heartbeat error:", err);
            }
        }, 120000); // 2 minutes

        return () => clearInterval(interval);
    }, [currentUser]);

    const value = {
        currentUser,
        signup,
        login,
        logout,
        updateUserProfile,
        resetPassword
    };

    return (
        <AuthContext.Provider value={value}>
            {!loading && children}
        </AuthContext.Provider>
    );
}

export function useAuth() {
    return useContext(AuthContext);
}

