  17 |         if (!currentUser) {
> 18 |             setIncomingCall(null);
     |             ^^^^^^^^^^^^^^^ Avoid calling setState() directly within an effect
  19 |             setActiveCall(null);
  20 |             setInCall(false);
  21 |             return;

