import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCall } from '../context/CallContext';
import { db } from "../firebase";
import { collection, query, where, orderBy, onSnapshot, addDoc, serverTimestamp, doc, setDoc, updateDoc, increment } from "firebase/firestore";
import { Send, ArrowLeft, MoreVertical, Video, Phone, MessageCircle } from "lucide-react";

export default function Chat() {
    const { currentUser } = useAuth();
    const { setInCall, setCallType } = useCall();
    const location = useLocation();

    const [chats, setChats] = useState([]);
    const [selectedChat, setSelectedChat] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState("");
    const [targetUserStatus, setTargetUserStatus] = useState(null);

    const messagesEndRef = useRef(null);
    const selectedChatRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    // Keep ref in sync with state for background notifications
    useEffect(() => {
        selectedChatRef.current = selectedChat;
    }, [selectedChat]);

    // Listen for Chats List & Local Notifications
    useEffect(() => {
        if (!currentUser) return;

        const q = query(
            collection(db, "chats"),
            where("participants", "array-contains", currentUser.uid)
        );

        const unsubscribe = onSnapshot(q, (snapshot) => {
            snapshot.docChanges().forEach((change) => {
                const data = change.doc.data();
                if (change.type === 'modified') {
                    const isNewMessage = data.lastMessageSenderId && data.lastMessageSenderId !== currentUser.uid;
                    const isNotFocused = document.visibilityState === 'hidden' || selectedChatRef.current?.id !== change.doc.id;
                    const isRecent = data.lastMessageTimestamp?.toMillis() > Date.now() - 5000;

                    if (isNewMessage && isNotFocused && isRecent && Notification.permission === 'granted') {
                        new Notification("New Message", {
                            body: data.lastMessage,
                            icon: '/pwa-192x192.png'
                        });
                    }
                }
            });

            const chatsData = snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            })).sort((a, b) => {
                const timeA = b.lastMessageTimestamp?.seconds || b.createdAt?.seconds || 0;
                const timeB = a.lastMessageTimestamp?.seconds || a.createdAt?.seconds || 0;
                return timeA - timeB;
            });
            setChats(chatsData);
        });

        return unsubscribe;
    }, [currentUser]);

    // Handle initial chat selection from navigation state
    useEffect(() => {
        if (location.state?.targetUser) {
            const { targetUser } = location.state;
            const participants = [currentUser.uid, targetUser.uid].sort();
            const chatId = participants.join('_');

            const participantData = {
                [currentUser.uid]: {
                    email: currentUser.email,
                    name: currentUser.displayName || currentUser.email.split('@')[0],
                    photo: currentUser.photoURL || null
                },
                [targetUser.uid]: {
                    email: targetUser.email,
                    name: targetUser.displayName || targetUser.email.split('@')[0],
                    photo: targetUser.photoURL || null
                }
            };

            const existingChat = chats.find(c => c.id === chatId);
            const sortedEmails = participants.map(uid => participantData[uid].email);
            const sortedNames = participants.map(uid => participantData[uid].name);
            const sortedPhotos = participants.map(uid => participantData[uid].photo);

            if (existingChat) {
                // Background update metadata if it changed
                const chatDocRef = doc(db, "chats", chatId);
                updateDoc(chatDocRef, {
                    participantNames: sortedNames,
                    participantPhotos: sortedPhotos
                }).catch(err => console.error("Error updating chat metadata:", err));

                setSelectedChat({
                    ...existingChat,
                    participantNames: sortedNames,
                    participantPhotos: sortedPhotos
                });
            } else {
                setSelectedChat({
                    id: chatId,
                    participants: participants,
                    participantEmails: sortedEmails,
                    participantNames: sortedNames,
                    participantPhotos: sortedPhotos,
                    listingData: location.state?.listingData || null,
                    isNew: true
                });
            }
        }
    }, [location.state, chats, currentUser]);

    // Listen for Messages in selected chat
    useEffect(() => {
        if (!selectedChat || selectedChat.isNew) {
            if (selectedChat?.isNew) setMessages([]);
            return;
        }

        const q = query(
            collection(db, "chats", selectedChat.id, "messages"),
            orderBy("timestamp", "asc")
        );

        const unsubscribe = onSnapshot(q, (snapshot) => {
            const msgs = snapshot.docs.map(doc => doc.data());
            setMessages(msgs);
            setTimeout(scrollToBottom, 50);
        });

        // Clear unread count
        const chatRef = doc(db, "chats", selectedChat.id);
        updateDoc(chatRef, {
            [`unreadCounts.${currentUser.uid}`]: 0
        }).catch(() => { });

        return unsubscribe;
    }, [selectedChat, currentUser.uid]);

    // Listen for Presence of target user
    useEffect(() => {
        if (!selectedChat) return;

        const targetUid = selectedChat.participants.find(p => p !== currentUser.uid);
        if (!targetUid) return;

        const unsubscribe = onSnapshot(doc(db, "users", targetUid), (doc) => {
            if (doc.exists()) {
                setTargetUserStatus(doc.data());
            }
        });

        return unsubscribe;
    }, [selectedChat, currentUser.uid]);

    const sendMessage = async (e) => {
        e.preventDefault();
        if (!newMessage.trim() || !selectedChat) return;

        const chatDocRef = doc(db, "chats", selectedChat.id);
        const text = newMessage;
        setNewMessage("");

        try {
            if (selectedChat.isNew) {
                await setDoc(chatDocRef, {
                    participants: selectedChat.participants,
                    participantEmails: selectedChat.participantEmails,
                    participantNames: selectedChat.participantNames,
                    participantPhotos: selectedChat.participantPhotos,
                    unreadCounts: {
                        [selectedChat.participants[0]]: 0,
                        [selectedChat.participants[1]]: 0
                    },
                    createdAt: serverTimestamp(),
                    lastMessage: text,
                    lastMessageSenderId: currentUser.uid,
                    lastMessageTimestamp: serverTimestamp()
                });

                const otherUser = selectedChat.participants.find(p => p !== currentUser.uid);
                await updateDoc(chatDocRef, {
                    [`unreadCounts.${otherUser}`]: increment(1)
                });

                setSelectedChat(prev => ({ ...prev, isNew: false }));
            } else {
                await updateDoc(chatDocRef, {
                    lastMessage: text,
                    lastMessageSenderId: currentUser.uid,
                    lastMessageTimestamp: serverTimestamp(),
                    [`unreadCounts.${selectedChat.participants.find(p => p !== currentUser.uid)}`]: increment(1)
                });
            }

            await addDoc(collection(db, "chats", selectedChat.id, "messages"), {
                text: text,
                senderId: currentUser.uid,
                timestamp: serverTimestamp()
            });

            scrollToBottom();
        } catch (error) {
            console.error("Error sending message:", error);
        }
    };

    const getOtherName = (chat) => {
        if (!chat) return "Unknown";
        const myIndex = chat.participants.indexOf(currentUser.uid);
        return chat.participantNames?.[1 - myIndex] || chat.participantEmails?.find(e => e !== currentUser.email) || "User";
    };

    const getOtherPhoto = (chat) => {
        if (!chat) return null;
        const myIndex = chat.participants.indexOf(currentUser.uid);
        return chat.participantPhotos?.[1 - myIndex];
    };

    const getStatusText = () => {
        if (!targetUserStatus?.lastSeen) return "Offline";
        const lastSeen = targetUserStatus.lastSeen.toDate();
        const diffMinutes = (new Date() - lastSeen) / 60000;

        if (diffMinutes < 5) return "Online";
        if (diffMinutes < 60) return `Last seen ${Math.floor(diffMinutes)}m ago`;
        if (diffMinutes < 1440) return `Last seen ${Math.floor(diffMinutes / 60)}h ago`;
        return `Last seen ${lastSeen.toLocaleDateString()}`;
    };

    const isOnline = targetUserStatus?.lastSeen && (new Date() - targetUserStatus.lastSeen.toDate()) / 60000 < 5;

    const startCall = (type) => {
        if (!selectedChat) return;
        setCallType(type);
        setInCall(true);
    };

    return (
        <div className="flex h-[calc(100vh-140px)] bg-white dark:bg-gray-900 rounded-3xl shadow-xl overflow-hidden border border-gray-100 dark:border-gray-800 transition-colors duration-300">
            {/* Sidebar List */}
            <div className={`w-full md:w-96 border-r border-gray-100 dark:border-gray-800 flex flex-col bg-gray-50/50 dark:bg-gray-950/50 ${selectedChat ? 'hidden md:flex' : 'flex'}`}>
                <div className="p-6 border-b border-gray-100 dark:border-gray-800 bg-white dark:bg-gray-900">
                    <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Messages</h2>
                </div>
                <div className="flex-1 overflow-y-auto p-4 space-y-2">
                    {chats.length === 0 ? (
                        <div className="text-center py-10 text-gray-500 dark:text-gray-400 flex flex-col items-center">
                            <MessageCircle className="mb-2 text-gray-300 dark:text-gray-700" size={40} />
                            <p>No conversations yet.</p>
                        </div>
                    ) : (
                        chats.map(chat => (
                            <button
                                key={chat.id}
                                onClick={() => setSelectedChat(chat)}
                                className={`w-full p-4 rounded-xl text-left transition-all duration-200 group relative ${selectedChat?.id === chat.id
                                    ? 'bg-blue-600 text-white shadow-lg shadow-blue-500/30'
                                    : 'bg-white dark:bg-gray-900 hover:bg-white dark:hover:bg-gray-800 hover:shadow-md border border-transparent hover:border-gray-100 dark:hover:border-gray-700 text-gray-900 dark:text-gray-100'
                                    }`}
                            >
                                <div className="flex items-center gap-3 mb-1">
                                    <div className="w-8 h-8 rounded-full flex-shrink-0 overflow-hidden bg-gray-100 dark:bg-gray-800 border border-gray-200 dark:border-gray-700">
                                        {getOtherPhoto(chat) ? (
                                            <img src={getOtherPhoto(chat)} alt="" className="w-full h-full object-cover" />
                                        ) : (
                                            <div className="w-full h-full flex items-center justify-center text-xs font-bold bg-blue-100 dark:bg-blue-900/30 text-blue-600 dark:text-blue-400">
                                                {getOtherName(chat)[0]?.toUpperCase()}
                                            </div>
                                        )}
                                    </div>
                                    <div className="flex-1 min-w-0">
                                        <h3 className={`font-bold text-sm flex justify-between items-center ${selectedChat?.id === chat.id ? 'text-white' : 'text-gray-900 dark:text-white'}`}>
                                            <span className="truncate">{getOtherName(chat)}</span>
                                            {chat.unreadCounts && chat.unreadCounts[currentUser.uid] > 0 && (
                                                <span className="flex-shrink-0 ml-2 w-2 h-2 bg-red-500 rounded-full"></span>
                                            )}
                                        </h3>
                                    </div>
                                </div>
                                <p className={`text-xs truncate opacity-80 ${selectedChat?.id === chat.id ? 'text-blue-100' : (chat.unreadCounts && chat.unreadCounts[currentUser.uid] > 0 ? 'text-gray-900 dark:text-white font-bold' : 'text-gray-500 dark:text-gray-400')}`}>
                                    {chat.lastMessageSenderId === currentUser.uid ? 'You: ' : ''}{chat.lastMessage}
                                </p>
                            </button>
                        ))
                    )}
                </div>
            </div>

            {/* Chat Area */}
            <div className={`flex-1 flex flex-col bg-white dark:bg-gray-900 ${!selectedChat ? 'hidden md:flex' : 'flex'}`}>
                {selectedChat ? (
                    <>
                        <div className="p-4 border-b border-gray-100 dark:border-gray-800 flex items-center gap-4 bg-white/80 dark:bg-gray-900/80 backdrop-blur z-10">
                            <button className="md:hidden p-2 -ml-2 text-gray-600 dark:text-gray-400" onClick={() => setSelectedChat(null)}>
                                <ArrowLeft size={20} />
                            </button>
                            <div className="w-10 h-10 rounded-full flex-shrink-0 flex items-center justify-center overflow-hidden border border-gray-100 dark:border-gray-800">
                                {getOtherPhoto(selectedChat) ? (
                                    <img src={getOtherPhoto(selectedChat)} alt={getOtherName(selectedChat)} className="w-full h-full object-cover" />
                                ) : (
                                    <div className="w-full h-full bg-blue-100 dark:bg-blue-900/30 text-blue-600 dark:text-blue-400 flex items-center justify-center font-bold">
                                        {getOtherName(selectedChat)[0]?.toUpperCase()}
                                    </div>
                                )}
                            </div>
                            <div>
                                <h3 className="font-bold text-gray-900 dark:text-white">{getOtherName(selectedChat)}</h3>
                                <p className={`text-xs font-medium flex items-center gap-1 ${isOnline ? 'text-green-600 dark:text-green-400' : 'text-gray-500 dark:text-gray-400'}`}>
                                    <span className={`w-1.5 h-1.5 rounded-full ${isOnline ? 'bg-green-500 animate-pulse' : 'bg-gray-400 dark:bg-gray-600'}`}></span>
                                    {getStatusText()}
                                </p>
                            </div>
                            <div className="flex items-center gap-2 ml-auto">
                                <button
                                    onClick={() => startCall('audio')}
                                    className="p-2.5 text-blue-600 dark:text-blue-400 hover:bg-blue-50 dark:hover:bg-blue-900/20 rounded-full transition-colors"
                                >
                                    <Phone size={20} />
                                </button>
                                <button
                                    onClick={() => startCall('video')}
                                    className="p-2.5 text-blue-600 dark:text-blue-400 hover:bg-blue-50 dark:hover:bg-blue-900/20 rounded-full transition-colors"
                                >
                                    <Video size={20} />
                                </button>
                                <button className="p-2 text-gray-400 hover:text-gray-600 dark:hover:text-gray-200">
                                    <MoreVertical size={20} />
                                </button>
                            </div>
                        </div>

                        <div className="flex-1 overflow-y-auto p-6 space-y-6 bg-slate-50 dark:bg-gray-950/50">
                            {selectedChat.listingData && (
                                <div className="flex justify-center mb-6">
                                    <div className="bg-white dark:bg-gray-900 p-3 rounded-2xl shadow-sm border border-gray-100 dark:border-gray-800 flex items-center gap-4 max-w-sm">
                                        <img src={selectedChat.listingData.image} alt="" className="w-16 h-16 rounded-lg object-cover" />
                                        <div>
                                            <p className="text-xs text-blue-600 font-bold uppercase tracking-wider mb-0.5">Inquiry about</p>
                                            <h4 className="font-bold text-gray-900 dark:text-white line-clamp-1">{selectedChat.listingData.title}</h4>
                                            <p className="text-sm font-medium text-gray-500">${selectedChat.listingData.price}</p>
                                        </div>
                                    </div>
                                </div>
                            )}
                            {messages.map((msg, index) => {
                                const isMe = msg.senderId === currentUser.uid;
                                return (
                                    <div key={index} className={`flex ${msg.isSystemMessage ? 'justify-center my-4' : (isMe ? 'justify-end' : 'justify-start')}`}>
                                        {msg.isSystemMessage ? (
                                            <span className="text-xs font-medium text-gray-500 dark:text-gray-400 bg-gray-100 dark:bg-gray-800 px-3 py-1 rounded-full">
                                                {msg.text}
                                            </span>
                                        ) : (
                                            <div className={`max-w-[75%] px-6 py-3 rounded-2xl shadow-sm text-sm ${isMe
                                                ? 'bg-blue-600 text-white rounded-tr-none'
                                                : 'bg-white dark:bg-gray-800 text-gray-800 dark:text-gray-200 border border-gray-100 dark:border-gray-700 rounded-tl-none'
                                                }`}>
                                                {msg.text}
                                            </div>
                                        )}
                                    </div>
                                );
                            })}
                            <div ref={messagesEndRef} />
                        </div>

                        <form onSubmit={sendMessage} className="p-4 bg-white dark:bg-gray-900 border-t border-gray-100 dark:border-gray-800">
                            <div className="flex gap-2 items-center bg-gray-50 dark:bg-gray-800 rounded-full px-2 py-2 border border-gray-200 dark:border-gray-700">
                                <input
                                    type="text"
                                    value={newMessage}
                                    onChange={(e) => setNewMessage(e.target.value)}
                                    placeholder="Type your message..."
                                    className="flex-1 bg-transparent border-none px-4 py-2 focus:ring-0 text-sm text-gray-900 dark:text-white"
                                />
                                <button type="submit" disabled={!newMessage.trim()} className="bg-blue-600 text-white rounded-full p-2 h-10 w-10 flex items-center justify-center disabled:opacity-50">
                                    <Send size={18} />
                                </button>
                            </div>
                        </form>
                    </>
                ) : (
                    <div className="flex-1 flex flex-col items-center justify-center text-gray-400 dark:text-gray-600 bg-slate-50/50 dark:bg-gray-950/20 font-medium">
                        <MessageCircle size={48} className="mb-2 opacity-20" />
                        <p>Pick a conversation to start chatting</p>
                    </div>
                )}
            </div>
        </div>
    );
}

import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';
import { db } from '../firebase';
import { collection, query, orderBy, onSnapshot } from 'firebase/firestore';
import { Search, MapPin, Tag } from 'lucide-react';
import CategoryPills from '../components/CategoryPills';

export default function Home() {
    const { /* currentUser */ } = useAuth();
    const [listings, setListings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState("");
    const [selectedCategory, setSelectedCategory] = useState("All");

    useEffect(() => {
        const q = query(collection(db, "listings"), orderBy("createdAt", "desc"));
        const unsubscribe = onSnapshot(q, (snapshot) => {
            const listingsData = snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            }));
            setListings(listingsData);
            setLoading(false);
        });

        return unsubscribe;
    }, []);

    const filteredListings = listings.filter(listing => {
        const matchesSearch = listing.title.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesCategory = selectedCategory === "All" || listing.category === selectedCategory;
        return matchesSearch && matchesCategory;
    });

    const container = {
        hidden: { opacity: 0 },
        show: {
            opacity: 1,
            transition: { staggerChildren: 0.1 }
        }
    };

    const item = {
        hidden: { opacity: 0, y: 20 },
        show: { opacity: 1, y: 0 }
    };

    return (
        <div className="space-y-8">
            {/* Hero / Header Section */}
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                <div>
                    <h1 className="text-3xl font-bold text-gray-900 dark:text-white tracking-tight">Discover</h1>
                    <p className="text-gray-500 dark:text-gray-400 mt-1">Found {filteredListings.length} items near you</p>
                </div>

                {/* Search Bar */}
                <div className="relative w-full md:w-96">
                    <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
                    <input
                        type="text"
                        placeholder="Search for items..."
                        className="w-full pl-10 pr-4 py-3 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 shadow-sm transition-all text-gray-900 dark:text-white"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </div>
            </div>

            <CategoryPills selected={selectedCategory} onSelect={setSelectedCategory} />

            {loading ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                    {[1, 2, 3, 4].map(i => (
                        <div key={i} className="bg-gray-100 dark:bg-gray-800 rounded-2xl h-64 animate-pulse"></div>
                    ))}
                </div>
            ) : (
                <motion.div
                    key={selectedCategory + searchTerm} // Force re-animation on filter change
                    variants={container}
                    initial="hidden"
                    animate="show"
                    className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6"
                >
                    {filteredListings.length === 0 ? (
                        <div className="col-span-full py-20 text-center">
                            <div className="w-24 h-24 bg-gray-100 dark:bg-gray-800 rounded-full flex items-center justify-center mx-auto mb-4">
                                <Search className="text-gray-400 dark:text-gray-500" size={40} />
                            </div>
                            <h3 className="text-lg font-medium text-gray-900 dark:text-white">No items found</h3>
                            <p className="text-gray-500 dark:text-gray-400 mt-1">Try adjusting your search or category.</p>
                        </div>
                    ) : (
                        filteredListings.map(listing => (
                            <motion.div key={listing.id} variants={item}>
                                <Link to={`/listing/${listing.id}`} className="group block bg-white dark:bg-gray-900 rounded-2xl shadow-sm hover:shadow-xl hover:-translate-y-1 transition-all duration-300 border border-gray-100 dark:border-gray-800 overflow-hidden h-full flex flex-col">
                                    <div className="relative aspect-[4/3] overflow-hidden bg-gray-100 dark:bg-gray-800">
                                        <img
                                            src={listing.imageUrl}
                                            alt={listing.title}
                                            className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-500"
                                        />
                                        <div className="absolute top-3 right-3">
                                            <span className="bg-white/90 dark:bg-gray-900/90 backdrop-blur text-xs font-bold px-2 py-1 rounded-lg shadow-sm dark:text-white">
                                                {listing.category}
                                            </span>
                                        </div>
                                    </div>
                                    <div className="p-4 flex flex-col flex-1">
                                        <div className="flex-1">
                                            <div className="flex justify-between items-start gap-2 mb-2">
                                                <h3 className="text-lg font-bold text-gray-900 dark:text-white group-hover:text-blue-600 transition-colors line-clamp-1">{listing.title}</h3>
                                                <p className="text-blue-600 dark:text-blue-400 font-bold whitespace-nowrap">Γéª{listing.price}</p>
                                            </div>
                                            <p className="text-sm text-gray-500 dark:text-gray-400 line-clamp-2 mb-4">{listing.description}</p>
                                        </div>
                                        <div className="pt-4 border-t border-gray-100 dark:border-gray-800 flex items-center justify-between text-xs text-gray-500 dark:text-gray-400">
                                            <div className="flex items-center gap-1 min-w-0 flex-1">
                                                <MapPin size={14} className="text-blue-500 flex-shrink-0" />
                                                <span className="truncate">{listing.address || (listing.location ? 'Map Available' : 'No Location')}</span>
                                            </div>
                                            <span className="ml-2 flex-shrink-0">{new Date(listing.createdAt?.seconds * 1000).toLocaleDateString()}</span>
                                        </div>
                                    </div>
                                </Link>
                            </motion.div>
                        ))
                    )}
                </motion.div>
            )}

        </div>
    );
}

import React, { useRef, useState } from "react";
import { useAuth } from "../context/AuthContext";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { Mail, Lock, ArrowRight, Loader, Eye, EyeOff } from "lucide-react";

export default function Login() {
    const emailRef = useRef();
    const passwordRef = useRef();
    const { login } = useAuth();
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const navigate = useNavigate();

    async function handleSubmit(e) {
        e.preventDefault();

        try {
            setError("");
            setLoading(true);
            await login(emailRef.current.value, passwordRef.current.value);
            navigate("/");
        } catch (error) {
            console.error("Login error:", error);
            setError("Failed to sign in. Please check your credentials.");
        }
        setLoading(false);
    }

    return (
        <div className="min-h-screen flex bg-gray-50 dark:bg-gray-950 transition-colors duration-300 overflow-hidden">
            {/* Visual Side */}
            <motion.div
                initial={{ x: -100, opacity: 0 }}
                animate={{ x: 0, opacity: 1 }}
                transition={{ duration: 0.6 }}
                className="hidden lg:flex lg:w-1/2 bg-gray-900 relative items-center justify-center p-12 text-white overflow-hidden"
            >
                <div className="absolute inset-0 opacity-20 bg-[radial-gradient(circle_at_top_right,_var(--tw-gradient-stops))] from-blue-500 via-indigo-500 to-gray-900 animate-pulse"></div>
                <div className="z-10 relative max-w-lg">
                    <h1 className="text-5xl font-extrabold mb-6 tracking-tight">Welcome Back.</h1>
                    <p className="text-xl text-gray-300 leading-relaxed">
                        Log in to access your messages, manage your listings, and continue your shopping journey.
                    </p>
                </div>
            </motion.div>

            {/* Form Side */}
            <div className="flex-1 flex items-center justify-center p-4 lg:p-12 relative">
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.2, duration: 0.5 }}
                    className="w-full max-w-md bg-white dark:bg-gray-900 rounded-3xl shadow-xl shadow-gray-200/50 dark:shadow-none p-8 border border-gray-100 dark:border-gray-800"
                >
                    <div className="text-center mb-10">
                        <div className="inline-flex items-center justify-center w-16 h-16 bg-blue-50 dark:bg-blue-900/20 rounded-2xl mb-4">
                            <Lock className="text-blue-600" size={30} />
                        </div>
                        <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Login</h2>
                        <p className="text-gray-500 dark:text-gray-400">Enter your credentials to continue</p>
                    </div>

                    {error && (
                        <motion.div
                            initial={{ height: 0, opacity: 0 }}
                            animate={{ height: 'auto', opacity: 1 }}
                            className="bg-red-50 dark:bg-red-900/20 text-red-600 dark:text-red-400 px-4 py-3 rounded-xl text-sm font-medium mb-6 flex items-center gap-2 border border-red-100 dark:border-red-900/20"
                        >
                            <span className="w-1.5 h-1.5 bg-red-500 rounded-full"></span>
                            {error}
                        </motion.div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-6">
                        <div>
                            <label className="block text-sm font-bold text-gray-700 dark:text-gray-300 mb-2 ml-1">Email Address</label>
                            <div className="relative">
                                <Mail className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
                                <input
                                    ref={emailRef}
                                    type="email"
                                    required
                                    className="w-full pl-12 pr-4 py-3.5 bg-gray-50 dark:bg-gray-800/50 border border-gray-200 dark:border-gray-700 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 transition-all font-medium text-gray-900 dark:text-white"
                                    placeholder="you@example.com"
                                />
                            </div>
                        </div>

                        <div>
                            <div className="flex justify-between items-center mb-2 ml-1">
                                <label className="block text-sm font-bold text-gray-700 dark:text-gray-300">Password</label>
                                <Link to="/forgot-password" size="sm" className="text-xs font-bold text-blue-600 hover:text-blue-700">
                                    Forgot password?
                                </Link>
                            </div>
                            <div className="relative">
                                <Lock className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
                                <input
                                    ref={passwordRef}
                                    type={showPassword ? "text" : "password"}
                                    required
                                    className="w-full pl-12 pr-12 py-3.5 bg-gray-50 dark:bg-gray-800/50 border border-gray-200 dark:border-gray-700 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 transition-all font-medium text-gray-900 dark:text-white"
                                    placeholder="Enter your password"
                                />
                                <button
                                    type="button"
                                    onClick={() => setShowPassword(!showPassword)}
                                    className="absolute right-4 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600 dark:hover:text-gray-200 focus:outline-none"
                                >
                                    {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                                </button>
                            </div>
                        </div>

                        <div className="pt-2">
                            <button
                                type="submit"
                                disabled={loading}
                                className="w-full bg-blue-600 text-white font-bold py-4 rounded-xl hover:bg-blue-700 transition-all shadow-lg shadow-blue-500/30 flex items-center justify-center gap-2 group"
                            >
                                {loading ? <Loader className="animate-spin" size={20} /> : (
                                    <>
                                        Sign In <ArrowRight size={20} className="group-hover:translate-x-1 transition-transform" />
                                    </>
                                )}
                            </button>
                        </div>
                    </form>

                    <div className="mt-8 text-center border-t border-gray-100 dark:border-gray-800 pt-8">
                        <p className="text-gray-500 dark:text-gray-400">
                            Don't have an account?{" "}
                            <Link to="/register" className="font-bold text-blue-600 hover:text-blue-700 hover:underline">
                                Sign up
                            </Link>
                        </p>
                    </div>
                </motion.div>
            </div>
        </div>
    );
}

import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { db } from '../firebase';
import { collection, query, where, getDocs } from 'firebase/firestore';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { User, Package, Calendar, MapPin, ArrowRight, Plus, Camera, Loader } from 'lucide-react';
import axios from 'axios';

const IMGBB_API_KEY = "5c96460dbce35dbdb36e2e26b2dad63e";

export default function Profile() {
    const { currentUser, updateUserProfile } = useAuth();
    const [myListings, setMyListings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [uploading, setUploading] = useState(false);

    useEffect(() => {
        async function fetchMyListings() {
            const q = query(
                collection(db, "listings"),
                where("userId", "==", currentUser.uid)
            );

            const querySnapshot = await getDocs(q);
            const data = querySnapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            })).sort((a, b) => b.createdAt?.seconds - a.createdAt?.seconds);
            setMyListings(data);
            setLoading(false);
        }

        if (currentUser) {
            fetchMyListings();
        }
    }, [currentUser]);

    const handleImageChange = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        setUploading(true);
        const formData = new FormData();
        formData.append("image", file);
        formData.append("key", IMGBB_API_KEY);

        try {
            const response = await axios.post("https://api.imgbb.com/1/upload", formData);
            const imageUrl = response.data.data.display_url;
            await updateUserProfile(currentUser, { photoURL: imageUrl });
        } catch (err) {
            console.error("Failed to upload profile picture:", err);
            alert("Failed to update profile picture.");
        }
        setUploading(false);
    };

    const container = {
        hidden: { opacity: 0 },
        show: {
            opacity: 1,
            transition: { staggerChildren: 0.1 }
        }
    };

    const item = {
        hidden: { opacity: 0, y: 20 },
        show: { opacity: 1, y: 0 }
    };

    return (
        <div className="max-w-5xl mx-auto space-y-8">
            {/* Profile Header */}
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="bg-white dark:bg-gray-900 rounded-3xl shadow-xl shadow-gray-200/50 dark:shadow-none overflow-hidden border border-gray-100 dark:border-gray-800 transition-colors"
            >
                <div className="h-32 bg-gradient-to-r from-blue-600 to-indigo-600 relative">
                    <div className="absolute -bottom-12 left-8">
                        <div className="relative group">
                            <div className="w-24 h-24 bg-white dark:bg-gray-800 rounded-2xl p-1 shadow-lg transition-colors">
                                <div className="w-full h-full bg-gray-100 dark:bg-gray-700 rounded-xl flex items-center justify-center overflow-hidden">
                                    {currentUser?.photoURL ? (
                                        <img src={currentUser.photoURL} alt="Profile" className="w-full h-full object-cover" />
                                    ) : (
                                        <div className="text-4xl text-gray-400">≡ƒæñ</div>
                                    )}
                                </div>
                            </div>

                            {/* Upload Overlay */}
                            <label className="absolute inset-0 flex items-center justify-center bg-black/50 rounded-2xl opacity-0 group-hover:opacity-100 cursor-pointer transition-opacity">
                                {uploading ? <Loader className="animate-spin text-white" /> : <Camera className="text-white" />}
                                <input type="file" className="hidden" accept="image/*" onChange={handleImageChange} disabled={uploading} />
                            </label>
                        </div>
                    </div>
                </div>
                <div className="pt-16 pb-8 px-8 flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
                    <div>
                        <h1 className="text-2xl font-bold text-gray-900 dark:text-white">{currentUser?.displayName || currentUser?.email?.split('@')[0]}</h1>
                        <p className="text-gray-500 dark:text-gray-400 font-medium">{currentUser?.email}</p>
                    </div>
                    <div className="flex gap-4">
                        <div className="bg-blue-50 dark:bg-blue-900/20 px-4 py-2 rounded-xl border border-blue-100 dark:border-blue-900/30 transition-colors">
                            <span className="block text-xs text-blue-600 dark:text-blue-400 font-bold uppercase tracking-wider">Active Listings</span>
                            <span className="text-2xl font-black text-gray-900 dark:text-white">{myListings.length}</span>
                        </div>
                        <Link to="/create-listing" className="bg-gray-900 dark:bg-blue-600 text-white px-6 py-2 rounded-xl font-bold flex items-center gap-2 hover:bg-black dark:hover:bg-blue-700 transition-colors shadow-lg shadow-gray-900/20 dark:shadow-blue-500/20">
                            <Plus size={20} /> New Listing
                        </Link>
                    </div>
                </div>
            </motion.div>

            {/* Listings Section */}
            <div>
                <h2 className="text-xl font-bold text-gray-900 dark:text-white mb-6 flex items-center gap-2">
                    <Package className="text-blue-600 dark:text-blue-400" /> My Inventory
                </h2>

                {loading ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                        {[1, 2, 3].map(i => (
                            <div key={i} className="bg-white dark:bg-gray-900 rounded-2xl h-80 animate-pulse shadow-sm border border-gray-100 dark:border-gray-800"></div>
                        ))}
                    </div>
                ) : (
                    <motion.div
                        variants={container}
                        initial="hidden"
                        animate="show"
                        className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
                    >
                        {myListings.length === 0 ? (
                            <div className="col-span-full py-20 text-center bg-white dark:bg-gray-900 rounded-3xl border border-dashed border-gray-300 dark:border-gray-700 transition-colors">
                                <div className="w-20 h-20 bg-gray-50 dark:bg-gray-800 rounded-full flex items-center justify-center mx-auto mb-4 text-gray-400 dark:text-gray-600">
                                    <Package size={32} />
                                </div>
                                <h3 className="text-lg font-bold text-gray-900 dark:text-white">No listings yet</h3>
                                <p className="text-gray-500 dark:text-gray-400 mb-6">Start selling your items today!</p>
                                <Link to="/create-listing" className="inline-flex items-center gap-2 text-blue-600 dark:text-blue-400 font-bold hover:underline">
                                    Create your first listing <ArrowRight size={16} />
                                </Link>
                            </div>
                        ) : (
                            myListings.map(listing => (
                                <motion.div key={listing.id} variants={item}>
                                    <Link to={`/listing/${listing.id}`} className="group block bg-white dark:bg-gray-900 rounded-2xl shadow-sm hover:shadow-xl hover:-translate-y-1 transition-all duration-300 border border-gray-100 dark:border-gray-800 overflow-hidden h-full flex flex-col">
                                        <div className="aspect-[4/3] bg-gray-100 dark:bg-gray-800 relative overflow-hidden">
                                            <img
                                                src={listing.imageUrl}
                                                alt={listing.title}
                                                className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-500"
                                            />
                                            <div className="absolute top-3 right-3">
                                                <span className="bg-white/90 dark:bg-gray-900/90 backdrop-blur text-xs font-bold px-2 py-1 rounded-lg shadow-sm text-gray-900 dark:text-white">
                                                    {listing.category}
                                                </span>
                                            </div>
                                        </div>
                                        <div className="p-5 flex flex-col flex-1">
                                            <div className="flex justify-between items-start mb-2">
                                                <h3 className="font-bold text-gray-900 dark:text-white line-clamp-1 group-hover:text-blue-600 transition-colors">{listing.title}</h3>
                                                <span className="text-blue-600 dark:text-blue-400 font-bold bg-blue-50 dark:bg-blue-900/20 px-2 py-0.5 rounded-md text-sm transition-colors">
                                                    Γéª{listing.price}
                                                </span>
                                            </div>

                                            <p className="text-sm text-gray-500 dark:text-gray-400 line-clamp-2 mb-4 flex-1 transition-colors">
                                                {listing.description}
                                            </p>

                                            <div className="pt-4 border-t border-gray-100 dark:border-gray-800 flex flex-col gap-3 transition-colors">
                                                <div className="flex items-center gap-1.5 text-xs text-gray-400 dark:text-gray-500 min-w-0">
                                                    <MapPin size={14} className="text-blue-500 flex-shrink-0" />
                                                    <span className="truncate">{listing.address || (listing.location ? 'Map Available' : 'No Location')}</span>
                                                </div>
                                                <div className="flex items-center justify-between text-xs font-medium text-gray-400 dark:text-gray-500">
                                                    <div className="flex items-center gap-1">
                                                        <Calendar size={14} />
                                                        {new Date(listing.createdAt?.seconds * 1000).toLocaleDateString()}
                                                    </div>
                                                    <div className="flex items-center gap-1 text-green-600 dark:text-green-400 bg-green-50 dark:bg-green-900/20 px-2 py-1 rounded-full transition-colors">
                                                        <span className="w-1.5 h-1.5 bg-green-500 rounded-full animate-pulse"></span>
                                                        Active
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </Link>
                                </motion.div>
                            ))
                        )}
                    </motion.div>
                )}
            </div>
        </div>

    );
}

import React, { useRef, useState } from "react";
import { useAuth } from "../context/AuthContext";
import { db } from "../firebase";
import { doc, setDoc, collection, query, where, getDocs } from "firebase/firestore";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { Mail, Lock, User, ArrowRight, Loader, Eye, EyeOff } from "lucide-react";

export default function Register() {
    const emailRef = useRef();
    const usernameRef = useRef(); // New ref for username
    const passwordRef = useRef();
    const passwordConfirmRef = useRef();
    const { signup, updateUserProfile } = useAuth(); // Import updateUserProfile
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    const navigate = useNavigate();

    async function handleSubmit(e) {
        e.preventDefault();

        if (passwordRef.current.value !== passwordConfirmRef.current.value) {
            return setError("Passwords do not match");
        }

        try {
            setError("");
            setLoading(true);

            const username = usernameRef.current.value;
            const email = emailRef.current.value;

            // Check if username already exists
            const q = query(collection(db, "users"), where("username", "==", username));
            const querySnapshot = await getDocs(q);

            if (!querySnapshot.empty) {
                setLoading(false);
                return setError("Username is already taken");
            }

            const userCredential = await signup(email, passwordRef.current.value);

            // Save user to Firestore to reserve username
            await setDoc(doc(db, "users", userCredential.user.uid), {
                username: username,
                email: email,
                createdAt: new Date()
            });

            // Update profile with username
            await updateUserProfile(userCredential.user, {
                displayName: username
            });

            navigate("/");
        } catch (error) {
            console.error("Registration error:", error);
            if (error.code === 'auth/email-already-in-use') {
                setError("Email is already currently in use");
            } else {
                setError("Failed to create an account");
            }
        }
        setLoading(false);
    }

    return (
        <div className="min-h-screen flex bg-gray-50 overflow-hidden">
            {/* Visual Side */}
            <motion.div
                initial={{ x: -100, opacity: 0 }}
                animate={{ x: 0, opacity: 1 }}
                transition={{ duration: 0.6 }}
                className="hidden lg:flex lg:w-1/2 bg-gray-900 relative items-center justify-center p-12 text-white overflow-hidden"
            >
                <div className="absolute inset-0 opacity-20 bg-[radial-gradient(circle_at_bottom_left,_var(--tw-gradient-stops))] from-blue-500 via-purple-500 to-gray-900 animate-pulse"></div>
                <div className="z-10 relative max-w-lg">
                    <h1 className="text-5xl font-extrabold mb-6 tracking-tight">Join Us.</h1>
                    <p className="text-xl text-gray-300 leading-relaxed">
                        Create your account today to start selling your items and discovering great deals in your neighborhood.
                    </p>
                </div>
            </motion.div>

            {/* Form Side */}
            <div className="flex-1 flex items-center justify-center p-4 lg:p-12 relative">
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.2, duration: 0.5 }}
                    className="w-full max-w-md bg-white rounded-3xl shadow-xl shadow-gray-200/50 p-8 border border-gray-100"
                >
                    <div className="text-center mb-10">
                        <h2 className="text-3xl font-bold text-gray-900 mb-2">Create Account</h2>
                        <p className="text-gray-500">Get started for free.</p>
                    </div>

                    {error && (
                        <motion.div
                            initial={{ height: 0, opacity: 0 }}
                            animate={{ height: 'auto', opacity: 1 }}
                            className="bg-red-50 text-red-600 px-4 py-3 rounded-xl text-sm font-medium mb-6 flex items-center gap-2 border border-red-100"
                        >
                            <span className="w-1.5 h-1.5 bg-red-500 rounded-full"></span>
                            {error}
                        </motion.div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-5">
                        <div>
                            <label className="block text-sm font-bold text-gray-700 mb-2 ml-1">Username</label>
                            <div className="relative">
                                <User className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
                                <input
                                    ref={usernameRef}
                                    type="text"
                                    required
                                    className="w-full pl-12 pr-4 py-3.5 bg-gray-50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 transition-all font-medium"
                                    placeholder="johndoe123"
                                />
                            </div>
                        </div>

                        <div>
                            <label className="block text-sm font-bold text-gray-700 mb-2 ml-1">Email</label>
                            <div className="relative">
                                <Mail className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
                                <input
                                    ref={emailRef}
                                    type="email"
                                    required
                                    className="w-full pl-12 pr-4 py-3.5 bg-gray-50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 transition-all font-medium"
                                    placeholder="you@example.com"
                                />
                            </div>
                        </div>

                        <div>
                            <label className="block text-sm font-bold text-gray-700 mb-2 ml-1">Password</label>
                            <div className="relative">
                                <Lock className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
                                <input
                                    ref={passwordRef}
                                    type={showPassword ? "text" : "password"}
                                    required
                                    className="w-full pl-12 pr-12 py-3.5 bg-gray-50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 transition-all font-medium"
                                    placeholder="Create a password"
                                />
                                <button
                                    type="button"
                                    onClick={() => setShowPassword(!showPassword)}
                                    className="absolute right-4 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600 focus:outline-none"
                                >
                                    {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                                </button>
                            </div>
                        </div>

                        <div>
                            <label className="block text-sm font-bold text-gray-700 mb-2 ml-1">Confirm Password</label>
                            <div className="relative">
                                <Lock className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
                                <input
                                    ref={passwordConfirmRef}
                                    type={showConfirmPassword ? "text" : "password"}
                                    required
                                    className="w-full pl-12 pr-12 py-3.5 bg-gray-50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 transition-all font-medium"
                                    placeholder="Repeat password"
                                />
                                <button
                                    type="button"
                                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                                    className="absolute right-4 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600 focus:outline-none"
                                >
                                    {showConfirmPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                                </button>
                            </div>
                        </div>

                        <div className="pt-2">
                            <button
                                type="submit"
                                disabled={loading}
                                className="w-full bg-blue-600 text-white font-bold py-4 rounded-xl hover:bg-blue-700 transition-all shadow-lg shadow-blue-500/30 flex items-center justify-center gap-2 group"
                            >
                                {loading ? <Loader className="animate-spin" size={20} /> : (
                                    <>
                                        Get Started <ArrowRight size={20} className="group-hover:translate-x-1 transition-transform" />
                                    </>
                                )}
                            </button>
                        </div>
                    </form>

                    <div className="mt-8 text-center">
                        <p className="text-gray-500">
                            Already have an account?{" "}
                            <Link to="/login" className="font-bold text-blue-600 hover:text-blue-700 hover:underline">
                                Log in
                            </Link>
                        </p>
                    </div>
                </motion.div>
            </div>
        </div>
    );
}
